@Module({
  imports: [RoutesModule, TypeOrmModule.forRoot({
    type: 'mongodb',
    host: Config.get().MONGO_URL,
    port: Config.get().MONGO_PORT,
    database : 'univ-cms',
    entities: ['src/**/*.entity.ts'],
    synchronize: true,
  })],
  controllers: [],
  providers: [
    AuthService,
    JwtStrategy,
    {
      provide: APP_GUARD,
      useClass: AuthGuard,
    },
    {
      provide: APP_FILTER,
      useClass: FunctionalExceptionFilter,
    },
    {
      provide: APP_FILTER,
      useClass: TechnicalExceptionFilter,
    }],
})
