  configure(consumer: MiddlewareConsumer) {
    consumer.apply(AuthMiddleware).forRoutes({
      path: '*', method: RequestMethod.ALL,
    });
  }
}

import { ExceptionFilter, Catch, ArgumentsHost } from '@nestjs/common';
import { FunctionalException } from './functional.exception';
import { WinLogger } from '../logger/winlogger';

@Catch(FunctionalException)
export class FunctionalExceptionFilter implements ExceptionFilter {

  private logger = WinLogger.get('functional-exception');

  catch(exception: FunctionalException, host: ArgumentsHost) {

    const ctx = host.switchToHttp();
    const response = ctx.getResponse();
    const request = ctx.getRequest();

    const status = exception.status;

    this.logger.error(`${exception.code} - ${exception.libelle}`);

    response
      .status(status)
      .json({
        statusCode: status,
        timestamp: new Date().toISOString(),
        path: request.url,
        code: exception.code,
        libelle: exception.libelle,
      });
  }
}

export class FunctionalException extends Error {

  constructor(public code: string,
              public libelle: string,
              public status?: string) {
    super();
    if (!status) {
      this.status = '400';
    }
  }

}

import { ExceptionFilter, Catch, ArgumentsHost } from '@nestjs/common';
import { TechnicalException } from './technical.exception';
import { WinLogger } from '../logger/winlogger';

@Catch(TechnicalException)
export class TechnicalExceptionFilter implements ExceptionFilter {

  private logger = WinLogger.get('technical-exception');

  catch(exception: TechnicalException, host: ArgumentsHost) {

    const ctx = host.switchToHttp();
    const response = ctx.getResponse();
    const request = ctx.getRequest();

    const status = exception.status;

    this.logger.error(exception.libelle, exception.error);

    response
      .status(status)
      .json({
        statusCode: status,
        timestamp: new Date().toISOString(),
        path: request.url,
        code: exception.code,
        libelle: exception.libelle,
        error: exception.error,
      });
  }
}

export class TechnicalException extends Error {

  constructor(public code: string,
              public libelle: string,
              public error?: any,
              public status?: string) {
    super();
    if (!status) {
      this.status = '400';
    }
  }

}

import { Logger, transports } from 'winston';
import { Config } from '../../config/config';

export class LoggerFactory {

  private static logger;

  public static get() {
    if (!this.logger) {
      this.logger = new Logger({
        transports: [
          new transports.Console({
            colorize: true,
            prettyPrint: true,
            level: Config.get().LOG_LEVEL,
            timestamp: true,
          }),
        ],
      });
    }
    return this.logger;
  }
}

import { LoggerInstance } from 'winston';
import { LoggerFactory } from './logger.factory';
import { LoggerService } from '@nestjs/common';

export class WinLogger implements LoggerService {

  private logger: LoggerInstance;

  private constructor(private name: string) {
    this.logger = LoggerFactory.get();
  }

  public static get(name): WinLogger {
    return new WinLogger(name);
  }

  debug(message: string, params?: any) {
    this.logger.debug(`[${this.name}]  -  ${message}`, params);
  }

  info(message: string, params?: any) {
    this.logger.info(`[${this.name}] - ${message}`, params);
  }

  warn(message: string, params?: any) {
    this.logger.warn(`[${this.name}]  - ${message}`, params);
  }

  error(message: string, params?: any) {
    this.logger.error(`[${this.name}]  -  ${message}`, params);
  }

  log(message: string) {
    this.logger.info(`[${this.name}]  -  ${message}`);
  }
}

import { PipeTransform, Injectable, ArgumentMetadata, BadRequestException } from '@nestjs/common';
import { validate } from 'class-validator';
import { plainToClass } from 'class-transformer';

@Injectable()
export class CustomValidationPipe implements PipeTransform<any> {

  constructor(private context: { transform: boolean } = { transform: true }) { }

  async transform(value, { metatype }: ArgumentMetadata) {
    if (!metatype || !this.toValidate(metatype)) {
      return value;
    }
    const object = plainToClass(metatype, value);
    const errors = await validate(object, { skipMissingProperties: true });
    if (errors.length > 0) {
      throw new BadRequestException('Validation failed', JSON.stringify(errors));
    }
    if (this.context.transform) {
      return object;
    } else {
      return value;
    }
  }

  private toValidate(metatype): boolean {
    const types = [String, Boolean, Number, Array, Object];
    return !types.find((type) => metatype === type);
  }
}

