@Injectable()
export class AuthMiddleware implements NestMiddleware {

  constructor(private readonly authService: AuthService) {}

  resolve(..._args: any[]): MiddlewareFunction {
    return (req: any, _res, next) => {
      this.authService.authenticate(req, next);
    };
  }
}

import { Injectable } from '@nestjs/common';
import { JwtStrategy } from './jwt.strategy';

@Injectable()
export class AuthService {

  constructor(private readonly jwtStrategy: JwtStrategy) { }

  public authenticate(req, next) {
    const jwt: any = this.jwtStrategy;
    jwt.success = (user) => {
      req.user = user;
      next();
    };
    jwt.fail = () => next();
    jwt.error = () => next();
    jwt.authenticate(req, { session: false });
  }
}

import { ExtractJwt, Strategy } from 'passport-jwt';
import { PassportStrategy } from '@nestjs/passport';
import { Injectable, UnauthorizedException } from '@nestjs/common';
import { Config } from '../config/config';
import { IncomingMessage } from 'http';

@Injectable()
export class JwtStrategy extends PassportStrategy(Strategy) {
  constructor() {
    super({
      jwtFromRequest: ExtractJwt.fromAuthHeaderAsBearerToken(),
      secretOrKey: Config.get().AUTH_JWT_KEY,
      passReqToCallback: true,
    });
  }

  async validate(_req: IncomingMessage, payload: any, done: (error, user) => void) {
    const user = payload;
    if (!user) {
      return done(new UnauthorizedException(), false);
    }
    done(null, user);
  }
}

import { Injectable, CanActivate, ExecutionContext } from '@nestjs/common';
import { Reflector } from '@nestjs/core';
import * as lodash from 'lodash';

@Injectable()
export class AuthGuard implements CanActivate {

  constructor(private readonly reflector: Reflector) { }

  canActivate(context: ExecutionContext): boolean {
    const controlerRoles: Array<string> = this.reflector.get<Array<string>>('roles', context.getClass()) || [];
    const methodeRoles: Array<string> = this.reflector.get<Array<string>>('roles', context.getHandler()) || [];
    const controlerAuthenticate: boolean = this.reflector.get<boolean>('authenticate', context.getClass()) || false;
    const methodeAuthenticate: boolean = this.reflector.get<boolean>('authenticate', context.getHandler()) || false;

    const roles: Array<string> = controlerRoles.concat(methodeRoles);
    const authenticate = controlerAuthenticate || methodeAuthenticate || !lodash.isEmpty(roles);

    if (!authenticate) {
      return true;
    }

    const request = context.switchToHttp().getRequest();
    const user = request.user;
    if (!user && authenticate) {
      return false;
    }
    if (!lodash.isEmpty(roles)) {
      return this.hasRoleOneOfRoles(user, roles);
    } else {
      // no security
      return true;
    }
  }

  private hasRoleOneOfRoles(user, roles): boolean {
    if (!user || !user.role) {
      return false;
    }
    return roles.includes(user.role);
  }
}

import { Repository, ObjectID } from 'typeorm';
import { TechnicalException } from '../common/exception/technical.exception';

export abstract class BaseService<E> {

  constructor(private repository: Repository<any>) { }

  async create(entity: any): Promise<any> {
    try {
      entity.createdOn = new Date();
      const insertResult = await this.repository.insert(entity);
      entity._id = insertResult.raw.insertedId;
      return entity;
    } catch (err) {
      throw new TechnicalException('mongo-error', err.message);
    }
  }

  async update(reference: string, entity: any): Promise<E> {
    entity.updatedOn = new Date();
    entity.reference = reference;
    try {
      await this.repository.update(this.condition('reference', reference), entity);
    } catch (err) {
      throw new TechnicalException('mongo-error', err.message);
    }
    return entity;
  }

  async delete(reference: string): Promise<boolean> {
    const result = await this.repository.delete(this.condition('reference', reference));
    return !!result;
  }

  findById(_id: ObjectID) {
    return this.repository.findOne({ where: { _id } });
  }

  findByReference(reference: string) {
    return this.repository.findOne({ where: { reference } });
  }

  private condition(name, value) {
    const literal = {};
    literal[name] = value;
    return literal;
  }
}

import { Injectable } from '@nestjs/common';
import { PageMongoRepository } from '../repository/repositories/page.repository';
import { BaseService } from './base.service';
import { PageEntity } from '../repository/schema/page.entity';
import { FunctionalException } from '../common/exception/functional.exception';
import * as shortid from 'shortid';

@Injectable()
export class PageService extends BaseService<PageEntity>{

  constructor(readonly pageRepository: PageMongoRepository) {
    super(pageRepository.getBaseRepository());
  }

  async create(page: PageEntity) {
    const existingPage = await this.pageRepository.getBaseRepository().findOne({name : page.name });
    if (existingPage) {
      throw new FunctionalException('already_exist', `A page with the same name already exist :  ${existingPage.name}`);
    }
    page.reference = shortid.generate();
    return super.create(page);
  }
}

